  | 'responsive-xl'
  | 'responsive-xxl';

export type VerticalAlignment =
  'align-baseline'
  | 'align-top'
  | 'align-middle'
  | 'align-bottom'
  | 'align-text-bottom'
  | 'align-text-top';

export type DropdownDirection = 'dropup' | 'dropend' | 'dropstart';

export type CarouselType = 'default' | 'three-cols' | 'three-cols-arrow-visible';

export interface Notification {

  /**
   * Notification type
   */
  type: NotificationType;

  /**
   * Notification title
   */
  title: string;

  /**
   * Notification message / text
   */
  message?: string;

  /**
   * Custom duration of notification
   */
  duration?: number;

  /**
   * The close notification button appears
   */
  dismissible?: boolean;

  /**
   * Custom position of notification
   */
  position?: NotificationPosition;

  /**
   * Custom icon of notification
   */
  icon?: IconName;
}

export enum NotificationType {
  Standard = 'standard',
  Success = 'success',
  Error = 'error',
  Info = 'info',
  Warning = 'warning'
}

export enum NotificationPosition {
  Top = 'top-fix mt-3',
  Bottom = 'bottom-fix mb-3',
  Left = 'left-fix ms-3',
  Right = 'right-fix me-3'
}

