import { Component, Input, Output, EventEmitter, ChangeDetectorRef, forwardRef, ChangeDetectionStrategy } from '@angular/core';
import { NG_VALUE_ACCESSOR, ControlValueAccessor } from '@angular/forms';

let identifier = 0;

export class CheckboxChange {
  source: CheckboxComponent;
  checked: boolean;
}

/**
 * Una checkbox con design bootstrap italia. Supporta tutte le funzionalità di una checkbox HTML5,
 * ed espone una API simile. Una `<it-checkbox>` può essere checked, unchecked, o disabled.
 */
@Component({
  selector: 'it-checkbox',
  templateUrl: './checkbox.component.html',
  styleUrls: ['./checkbox.component.css'],
  providers: [{ provide: NG_VALUE_ACCESSOR, useExisting: forwardRef(() => CheckboxComponent), multi: true }],
  changeDetection: ChangeDetectionStrategy.OnPush
})
export class CheckboxComponent implements ControlValueAccessor {
  /**
   * Se la checkbox è selezionata.
   */
  @Input()
  get checked(): boolean { return this._checked; }
  set checked(value: boolean) {
    if (value !== this.checked) {
      this._checked = value;
      this._changeDetectorRef.markForCheck();
    }
  }
  private _checked = false;

  /**
   * L'etichetta della checkbox.
   */
  @Input()
  label: string;

  /**
   * Se la checkbox è disabilitata.
   */
  @Input()
  get disabled(): boolean { return this._disabled; }
  set disabled(value: boolean) {
    if (value !== this.disabled) {
      this._disabled = value;
      this._changeDetectorRef.markForCheck();
    }
  }
  private _disabled = false;

  /**
   * Evento emesso quando il valore `checked` della checkbox cambia.
   */
  @Output() readonly change: EventEmitter<CheckboxChange> =
    new EventEmitter<CheckboxChange>();

    inputId = `checkbox-${identifier++}`;

  private _onTouched: () => any = () => {};

  private _controlValueAccessorChangeFn: (value: any) => void = () => { };

  constructor(
    private _changeDetectorRef: ChangeDetectorRef
  ) { }

  writeValue(value: any) {
    this.checked = !!value;
  }

  registerOnChange(fn: (value: any) => void) {
    this._controlValueAccessorChangeFn = fn;
  }

  registerOnTouched(fn: any) {
    this._onTouched = fn;
  }

  handleChange(event: Event) {
    event.stopPropagation();
    if (!this.disabled) {
      this._toggle();
      this._emitChangeEvent();
    }
  }

  private _toggle(): void {
    this.checked = !this.checked;
  }

  private _emitChangeEvent() {
    const event = new CheckboxChange();
    event.source = this;
    event.checked = this.checked;

    this._controlValueAccessorChangeFn(this.checked);
    this.change.emit(event);
  }
}

import {
    Injector,
    TemplateRef,
    ViewRef,
    ViewContainerRef,
    Renderer2,
    ComponentRef,
    ComponentFactoryResolver
} from '@angular/core';

export class ContentRef {
    constructor(public nodes: any[], public viewRef?: ViewRef, public componentRef?: ComponentRef<any>) { }
}

export class PopupService<T> {
    private _windowRef: ComponentRef<T>;
    private _contentRef: ContentRef;

    constructor(
        private _type: any, private _injector: Injector, private _viewContainerRef: ViewContainerRef,
        private _renderer: Renderer2, private _componentFactoryResolver: ComponentFactoryResolver) { }

    open(content?: string | TemplateRef<any>, context?: any): ComponentRef<T> {
        if (!this._windowRef) {
            this._contentRef = this._getContentRef(content, context);
            this._windowRef = this._viewContainerRef.createComponent(
                this._componentFactoryResolver.resolveComponentFactory<T>(this._type), 0, this._injector,
                this._contentRef.nodes);
        }

        return this._windowRef;
    }

    close() {
        if (this._windowRef) {
            this._viewContainerRef.remove(this._viewContainerRef.indexOf(this._windowRef.hostView));
            this._windowRef = null;

            if (this._contentRef.viewRef) {
                this._viewContainerRef.remove(this._viewContainerRef.indexOf(this._contentRef.viewRef));
                this._contentRef = null;
            }
        }
    }

    private _getContentRef(content: string | TemplateRef<any>, context?: any): ContentRef {
        if (!content) {
            return new ContentRef([]);
        } else if (content instanceof TemplateRef) {
            const viewRef = this._viewContainerRef.createEmbeddedView(<TemplateRef<T>>content, context);
            return new ContentRef([viewRef.rootNodes], viewRef);
        } else {
            return new ContentRef([[this._renderer.createText(`${content}`)]]);
        }
    }
}

